    <div>
        <h1>{props.heading}</h1>
        <div className="mb-3">
            <textarea className="form-control" value={text} id="myBox" rows="8"></textarea>
        </div>
        <button className="btn btn-primary">Convert to Upper case</button>

    </div>
  )
}
